mapper.select:
SELECT rno, pno, mid, rating, review_content, regDate, modDate
FROM tbl_review
WHERE rno = #{rno}

mapper.update:
UPDATE tbl_review
SET rating = #{rating}, review_content = #{reviewContent}, modDate = current_TIMESTAMP
WHERE rno = #{rno} AND mid = #{mid}

mapper.select:
SELECT AVG(rating) as averageRating, COUNT(rno) as reviewContent
FROM tbl_review
WHERE pno = #{pno}
